vertex:
@vertex
fn vs_main(@builtin(vertex_index) vi: u32) -> VSOut {
    var out: VSOut;
    let x = f32((vi & 1u) << 2u);
    let y = f32((vi & 2u) << 1u);
    out.pos = vec4<f32>(x - 1.0, 1.0 - y, 0.0, 1.0);
    out.uv = vec2<f32>(x * 0.5, y * 0.5);
    return out;
}

fragment:
@fragment
fn fs_main(in: VSOut) -> @location(0) vec4<f32> {
    // Sample the image from iChannel0
    let color = textureSample(iChannel0, iChannel0Sampler, in.uv);

    // Apply gamma correction
    let corrected = applyGamma(color.rgb, uniforms.gamma);

    return vec4<f32>(corrected, color.a);
}
