@fragment
fn fs_main(in: VSOut) -> @location(0) vec4<f32> {
    // Sample from iChannel0 (user-loaded image)
    let col = textureSample(iChannel0, iChannel0Sampler, in.uv);

    // Mix with audio-reactive effect
    let bass = uniforms.audio_bass;
    let tint = vec3<f32>(1.0, 0.5 + bass * 0.5, 0.5);

    return vec4<f32>(col.rgb * tint, 1.0);
}
